var fuseOptions={shouldSort:true,includeMatches:true,threshold:0.0,tokenize:true,location:0,distance:100,maxPatternLength:32,minMatchCharLength:1,keys:[{name:"title",weight:0.8},{name:"description",weight:0.5},{name:"tags",weight:0.3},{name:"categories",weight:0.3}]};var searchQuery=param("s");if(searchQuery){$("#search-query").val(searchQuery);executeSearch(searchQuery);}
function executeSearch(searchQuery){$.getJSON("/index.json",function(data){var pages=data;var fuse=new Fuse(pages,fuseOptions);var result=fuse.search(searchQuery);if(result.length>0){populateResults(result);}else{$('#search-results').append("<p>Sans r√©sultat</p>");}});}
function populateResults(result){$.each(result,function(key,value){var snippetHighlights=[];var tags=[];if(fuseOptions.tokenize){snippetHighlights.push(searchQuery);}else{$.each(value.matches,function(matchKey,mvalue){if(mvalue.key=="tags"||mvalue.key=="categories"||mvalue.key=="description"){snippetHighlights.push(mvalue.value);}});}
var templateDefinition=$('#search-result-template').html();var output=render(templateDefinition,{key:key,title:value.item.title,link:value.item.permalink,tags:value.item.tags,categories:value.item.categories,description:value.item.description});$('#search-results').append(output);$.each(snippetHighlights,function(snipkey,snipvalue){$("#summary-"+key).mark(snipvalue);});});}
function param(name){return decodeURIComponent((location.search.split(name+'=')[1]||'').split('&')[0]).replace(/\+/g,' ');}
function render(templateString,data){var conditionalMatches,conditionalPattern,copy;conditionalPattern=/\$\{\s*isset ([a-zA-Z]*) \s*\}(.*)\$\{\s*end\s*}/g;copy=templateString;while((conditionalMatches=conditionalPattern.exec(templateString))!==null){if(data[conditionalMatches[1]]){copy=copy.replace(conditionalMatches[0],conditionalMatches[2]);}else{copy=copy.replace(conditionalMatches[0],'');}}
templateString=copy;var key,find,re;for(key in data){find='\\$\\{\\s*'+key+'\\s*\\}';re=new RegExp(find,'g');templateString=templateString.replace(re,data[key]);}
return templateString;}